export class DemoDatepickerBasicComponent {}
export class PartidasDetalhesComponent {
  constructor(fb, localeService, activatedRouter, partidaService, treinoService, mapaService, callService, modoService, submodoService, jogadorService, spiner, toaster, router, modalService, estatisticaService) {
    this.fb = fb;
    this.localeService = localeService;
    this.activatedRouter = activatedRouter;
    this.partidaService = partidaService;
    this.treinoService = treinoService;
    this.mapaService = mapaService;
    this.callService = callService;
    this.modoService = modoService;
    this.submodoService = submodoService;
    this.jogadorService = jogadorService;
    this.spiner = spiner;
    this.toaster = toaster;
    this.router = router;
    this.modalService = modalService;
    this.estatisticaService = estatisticaService;
    this.currentDate = new Date(); //Data atual, usado no datapicker

    this.maxFormsEstatistica = 4; //Variavel de controle de quantodade de estatisticas
    //SelectedOptions

    this.soTreino = '0';
    this.soMapa = '0';
    this.soCall = '0';
    this.soModo = '0';
    this.soSubModo = '0';
    this.soJogador = '0'; // soJogador1: string ='0';
    // soJogador2: string ='0';
    // soJogador3: string ='0';
    // soJogador4: string ='0';

    this.treino = [];
    this.mapas = [];
    this.modos = [];
    this.smodos = [];
    this.calls = [];
    this.jogador = [];
    this.partida = {};
    this.modeSave = 'postPartida';
    this.estatisticaAtual = {
      id: 0,
      indice: 0
    };
    this.localeService.use('pt-br');
  }

  get modoEditar() {
    return this.modeSave === 'putPartida';
  }

  get estatisticas() {
    return this.form.get('estatisticas');
  }

  get f() {
    return this.form.controls;
  }

  get bsConfig() {
    return {
      isAnimated: true,
      adaptivePosition: true,
      dateInputFormat: 'DD/MM/YYYY',
      containerClass: 'theme-default',
      showWeekNumbers: false,
      initialValue: this.currentDate
    };
  }

  carregaDados() {
    this.partidaId = +this.activatedRouter.snapshot.paramMap.get('id');

    if (this.partidaId !== null && this.partidaId > 0) {
      this.spiner.show();
      this.modeSave = 'putPartida';
      this.partidaService.getPartidaById(this.partidaId).subscribe(partida => {
        this.partida = Object.assign({}, partida);
        this.form.patchValue(this.partida);
        this.partida.estatisticas.forEach(estatistica => {
          this.estatisticas.push(this.criarEstatisticas(estatistica));
        }); //this.carregaEstatisticas();
      }, Error => {
        console.error(Error);
        this.toaster.error('Erro ao carregar partida', 'Erro!');
      }).add(() => {
        this.spiner.hide();
      });
    }
  } // public carregaEstatisticas():void{
  //     this.estatisticaService.getEstatisticasByPartidaId(this.partidaId).subscribe(
  //       (estatisticaRetorno: Estatistica[]) =>{
  //         estatisticaRetorno.forEach(estatistica =>{ 
  //           this.estatisticas.push(this.criarEstatisticas(estatistica))
  //         }
  //       )
  //       },
  //       (error :any) =>{
  //         this.toaster.error('Erro ao tentar carregar as Estatisticas', 'Erro!');
  //         console.error(error);
  //       }
  //     ).add(()=>{ this.spiner.hide();},)
  // }


  getCall() {
    if (this.soMapa == '0') {
      this.callService.getCalls().subscribe(call => {
        this.calls = call;
      }, error => {});
    } else {
      this.callService.getCallByMapaId(Number(this.soMapa)).subscribe(call => {
        this.calls = call;
      }, error => {});
    }
  }

  getTreinos() {
    this.treinoService.getTreinos().subscribe(treino => {
      this.treino = treino;
    }, error => {});
  }

  getMapas() {
    this.mapaService.getMapas().subscribe(mapa => {
      this.mapas = mapa;
    }, error => {});
  }

  getModo() {
    this.modoService.getModos().subscribe(modo => {
      this.modos = modo;
    }, error => {});
  }

  getSubModo() {
    if (this.soModo == '0') {
      this.submodoService.getSubModos().subscribe(sm => {
        this.smodos = sm;
      }, error => {});
    } else this.submodoService.getSubModoByModoId(Number(this.soModo)).subscribe(sm => {
      this.smodos = sm;
    }, error => {});
  }

  getJogadores() {
    this.jogadorService.getJogadores().subscribe(jg => {
      this.jogador = jg;
    }, error => {});
  }

  onSelecionaMapa() {
    this.getCall();
  }

  onSelecionaModo() {
    this.getSubModo();
  }

  getFullNgModelName(index) {
    return `${this.soJogador}${index}`;
  }

  ngOnInit() {
    this.getJogadores();
    this.getTreinos();
    this.getMapas();
    this.getCall();
    this.getModo();
    this.getSubModo();
    this.carregaDados();
    this.validation();
  }

  validation() {
    this.form = this.fb.group({
      partidaDescricao: ['', [Validators.required, Validators.minLength(4), Validators.maxLength(20)]],
      partidaData: ['', [Validators.required]],
      posicao: ['', [Validators.required, Validators.min(1), Validators.max(55)]],
      treinoId: ['', [Validators.required]],
      mapaId: ['', [Validators.required]],
      callId: ['', [Validators.required]],
      modoId: ['', [Validators.required]],
      submodoId: ['', [Validators.required]],
      estatisticas: this.fb.array([])
    });
  }

  adicionarEstatisticas() {
    if (this.estatisticas.length < this.maxFormsEstatistica) this.estatisticas.push(this.criarEstatisticas({
      id: 0
    }));
  }

  buttonAdicionarDisabled() {
    return this.estatisticas.length < this.maxFormsEstatistica;
  }

  criarEstatisticas(estatistica) {
    return this.fb.group({
      id: [estatistica.id],
      jogadorId: [estatistica.jogadorId, Validators.required],
      kill: [estatistica.kill, Validators.required],
      morte: [estatistica.morte, Validators.required],
      assistencia: [estatistica.assistencia, Validators.required],
      dano: [estatistica.dano, Validators.required],
      derrubados: [estatistica.derrubados, Validators.required],
      cura: [estatistica.cura, Validators.required],
      //levantados: [estatistica.levantados, Validators.required],
      ressucitou: [estatistica.ressucitou, Validators.required],
      tempo: [estatistica.tempo, Validators.required]
    });
  }

  resetForm() {
    this.form.reset();
  }

  cssValidator(campoForm) {
    return {
      'is-invalid': campoForm.errors && campoForm.touched
    };
  }

  salvarPartida() {
    this.spiner.show();

    if (this.form.valid) {
      this.partida = this.modeSave === 'postPartida' ? Object.assign({}, this.form.value) : Object.assign({
        id: this.partida.id
      }, this.form.value);
      this.partidaService[this.modeSave](this.partida).subscribe(partidaRetorno => {
        this.toaster.success('Partida Salva com Sucesso', 'Sucesso!');
        this.router.navigate([`partidas/detalhes/${partidaRetorno.id}`]);
      }, error => {
        console.error(error);
        this.toaster.error('Erro ao salvar a partida', 'Error');
      }).add(() => this.spiner.hide());
    }
  }

  salvarEstatisticas() {
    this.spiner.show();

    if (this.form.controls['estatisticas'].valid) {
      this.estatisticaService.saveEstatistica(this.partidaId, this.form.value.estatisticas).subscribe(() => {
        this.toaster.success('Estatisticas salvos com Sucesso!', 'Sucesso!'); //this.estatisticas.reset();
      }, error => {
        this.toaster.error('Erro ao tentar Salvar as Estatisticas', 'Error!');
        console.error(error);
      }).add(() => this.spiner.hide());
    }
  }

  removerEstatistica(template, indice) {
    this.estatisticaAtual.id = this.estatisticas.get(indice + '.id').value;
    this.estatisticaAtual.indice = indice;
    if (this.estatisticaAtual.id !== 0) this.modalRef = this.modalService.show(template, {
      class: 'modal-sm'
    });else this.estatisticas.removeAt(this.estatisticaAtual.indice);
  }

  confirm() {
    this.modalRef.hide();
    this.spiner.show();
    this.estatisticaService.deleteEstatistica(this.partidaId, this.estatisticaAtual.id).subscribe(() => {
      this.toaster.success('Estatistica deletada com sucesso', 'Sucesso');
      this.estatisticas.removeAt(this.estatisticaAtual.indice);
    }, error => {
      this.toaster.error('Erro ao deletar a estatistica', 'Erro');
      console.error(error);
    }).add(() => this.spiner.hide());
  }

  decline() {
    this.modalRef.hide();
  }

}

